/* * BREAKPOINTS (start) */var desktop = 1600;var notebook = 1200;var tablet_wide = 1024;var tablet_tall = 768;var phone_wide = 480;var phone_tall = 320;var footer_icon = $('#footer i');$(window).setBreakpoints({	breakpoints: [desktop, notebook, tablet_wide, tablet_tall, phone_wide, phone_tall]});$(window).bind('enterBreakpoint' + desktop, function() {	footer_icon.addClass('icon-desktop');});$(window).bind('exitBreakpoint' + desktop, function() {	footer_icon.removeClass('icon-desktop');});$(window).bind('enterBreakpoint' + notebook, function() {	footer_icon.addClass('icon-laptop');});$(window).bind('exitBreakpoint' + notebook, function() {	footer_icon.removeClass('icon-laptop');});$(window).bind('enterBreakpoint' + tablet_wide, function() {	footer_icon.addClass('icon-tablet icon-rotate-right');	footer_icon.addClass('icon-rotate-right');});$(window).bind('exitBreakpoint' + tablet_wide, function() {	footer_icon.removeClass('icon-tablet');	footer_icon.removeClass('icon-rotate-right');});$(window).bind('enterBreakpoint' + tablet_tall, function() {	footer_icon.addClass('icon-tablet');});$(window).bind('exitBreakpoint' + tablet_tall, function() {	footer_icon.removeClass('icon-tablet');});$(window).bind('enterBreakpoint' + phone_wide, function() {	footer_icon.addClass('icon-mobile-phone');	footer_icon.addClass('icon-rotate-right');});$(window).bind('exitBreakpoint' + phone_wide, function() {	footer_icon.removeClass('icon-mobile-phone');	footer_icon.removeClass('icon-rotate-right');});$(window).bind('enterBreakpoint' + phone_tall, function() {	footer_icon.addClass('icon-mobile-phone');});$(window).bind('exitBreakpoint' + phone_tall, function() {	footer_icon.removeClass('icon-mobile-phone');});